import React, { Component } from "react"
import TodoItem from "./TodoItem.js"
import NewTodoForm from "./NewTodoForm.js"

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todoItems: ["Do homework", "Do luandry"]
        }
    }

    addTodo(todo) {
        this.setState(currentState => [...currentState.todoItems, todo])
    }

    render() {
        return (
            <div>
                <ul>
                    {this.state.todoItems.map(item => <TodoItem todoContent={item} />)}
                </ul>
                <NewTodoForm />
            </div>
        )
    }
}

export default TodoList
