import React, { Component } from 'react'

class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = { isEditing: false, editedTask: this.props.todoContent };
        this.handleRemove = this.handleRemove.bind(this);
        this.renderEditForm = this.renderEditForm.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
    }

    handleRemove() {
        this.props.removeTodo(this.props.id);
    }

    handleEdit(event) {
        event.preventDefault();
        this.setState({ editedTask: event.target.value })

    }

    renderEditForm() {
        this.setState({ isEditing: true });
    }

    render() {
        let renderMessage;
        if (this.state.isEditing) {
            renderMessage =
                <form onSubmit={this.handleEdit}>
                    <input type="text" value={this.state.editedTask} />
                    <button>Save</button>
                </form>
        } else {
            renderMessage =
                <div>
                    <li>{this.props.todoContent}</li>
                    <button onClick={this.handleRemove}>x</button>
                    <button onClick={this.renderEditForm}>Edit</button>
                </div>
        }
        return renderMessage;
    }
}

export default TodoItem;
