import React, { Component } from "react"
import TodoItem from "./TodoItem.js"
import NewTodoForm from "./NewTodoForm.js"
import { v4 as uuidv4 } from 'uuid';

class TodoList extends Component {
    constructor(props) {
        super(props);
        this.state = { todoItems: [{ todo: "Do homework", id: uuidv4() }, { todo: "Do luandry", id: uuidv4() }] }
        this.addTodo = this.addTodo.bind(this);
    }

    addTodo(todo) {
        this.setState({ todoItems: [...this.state.todoItems, todo] });
    }

    removeTodo(todo) {
        this.setState({ todoItems: this.state.todoItems.filter(item => item.id !== todo.id) });
    }

    render() {
        return (
            <div>
                <ul>
                    {this.state.todoItems.map(item => <TodoItem key={item.id} todoContent={item.todo} />)}
                </ul>
                <NewTodoForm addTodo={this.addTodo} />
            </div>
        )
    }
}

export default TodoList
